import React from 'react';
import styled from 'styled-components';
import PText from './PText';
import Button from './Button';
import AboutImg from '../assets/haut.jpg';

const AboutPageStyles = styled.div`
  padding: 20rem 0 10rem 0;

  .top-section {
    display: flex;
    align-items: center;
    justify-content: center;
    gap: 2rem;
  }
  .left {
    flex: 3;
  }
  .right {
    flex: 2;
  }
  .about__subheading {
    font-size: 2.2rem;
    margin-bottom: 2rem;
    span {
      background-color: var(--deep-dark);
      padding: 0.5rem;
      border-radius: 8px;
    }
  }
  .about__heading {
    font-size: 3.6rem;
    margin-bottom: 3rem;
  }
  .about__info {
    margin-bottom: 4rem;
    .para {
      max-width: 100%;
    }
  }
  .right {
    img {
      border: 2px solid var(--gray-1);
    }
  }
  .about__info__items {
    margin-top: 15rem;
  }
  .about__info__item {
    margin-bottom: 10rem;
  }
  .about__info__heading {
    font-size: 3.6rem;
    text-transform: uppercase;
  }
  @media only screen and (max-width: 768px) {
    padding: 10rem 0;
    .top-section {
      flex-direction: column;
      gap: 5rem;
    }
    .about__subheading {
      font-size: 1.8rem;
    }
    .about__heading {
      font-size: 2.8rem;
    }
    .about__info__heading {
      font-size: 3rem;
    }
  }
`;

export default function HauDePage() {
  return (
    <>
      <AboutPageStyles>
        <div className="container">
          <div className="top-section">
            <div className="right">
              <div className="about__info">
                <PText>
                  Cette phase initiale est certainement la plus lourde et
                  concerne en particulier les travaux de : Rénovation de la
                  couverture
                  <br /> <br />
                  Si votre couverture est en mauvais état, il est plus que
                  souhaitable de commencer par elle afin de mettre votre maison
                  au sec pour un bon moment car la durée de vie d’une couverture
                  traditionnelle neuve (ardoises, tuiles, …) est d’environ 50
                  ans contre environ 25 ans pour une étanchéité goudron. Dans le
                  cas d’une étanchéité à membrane EPDM (cas des toits plats), la
                  durée de vie de celle-ci est équivalente à une couverture
                  traditionnelle.s
                </PText>
              </div>
              <Button btnText="Download CV" btnLink="#" />
            </div>
            <div className="left">
              <img src={AboutImg} alt="me" />
            </div>
          </div>
        </div>
      </AboutPageStyles>
    </>
  );
}
